    public function index(Request $request)
    {
        if(!empty($request->get('department'))){
            $department = $request->get('department');
        }
        else{
            $department = 'all';
        }
        $current_department = $department;
        $user = Auth::user();
        $users = User::whereIn('role_id', ['1', '2', '5', '6', '7'])->get();
        $settings = Setting::all();
        $current_route = $department;
        $current_carbon_route = str_replace('task_', '', $current_route);


        $total_camp = Campaigns::all()->count();
        $total_creative_task = CreativeTask::all()->count();
        $total_paid_task = TaskPaid::all()->count();
        $total_seo_task = TaskSEO::all()->count();
        $total_web_task = TaskWeb::all()->count();
        $total_lms_task = TaskLMS::all()->count();
        return view('home', compact(
            'current_department',
            'user',
            'users',
            'settings',
            'current_route',
            'current_carbon_route',

            'total_camp',
            'total_creative_task',
            'total_paid_task',
            'total_seo_task',
            'total_web_task',
            'total_lms_task'
        ));
    }
    public function fire_url($url){
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_URL, $url);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
        $output = curl_exec($ch);
        curl_close($ch);
    }
