        // if(Auth::check()){
            config(['imap.accounts.default.host' => 'imap.gmail.com']);
            config(['imap.accounts.default.port' => 993]);
            config(['imap.accounts.default.username' => Auth::user()->email]);
            config(['imap.accounts.default.password' => Auth::user()->gsuite_password]);
        // }
            // echo config('imap.accounts.default.username');
            // echo config('imap.accounts.default.password');
            // echo Auth::user()->email;
        $logged_user = Auth::user();
        $update_user = User::find($logged_user->id);
        $last_email_update = $update_user->email_last_check;
        $last_update_time = Carbon::now();
        $client = Client::account('default');
        $client->connect();
        $folders = $client->getFolders();
        foreach($folders as $folder){
            $messages = $folder->query()->since($update_user->email_last_check)->get();
                // echo '<table><tbody>';
            foreach($messages as $message){
                // echo '<tr>';
                // echo '<td>'.$message->getDate().'</td>';
                // echo '<td>'.json_encode($message->getFrom()).'</td>';
                // echo '<td>'.json_encode($message->getTo()).'</td>';
                // echo '<td>'.json_encode($message->getCc()).'</td>';
                // echo '<td>'.json_encode($message->getBcc()).'</td>';
                // echo '<td>'.$message->getSubject().'</td>';
                // echo '<td>'.$message->getTextBody().'</td>';
                // echo '<td>'.$message->getHTMLBody().'</td>';
                // echo '</tr>';
                $from_info = $message->getFrom();
                if(!empty($from_info[0]->personal)){
                    $from_name = $from_info[0]->personal;
                }
                elseif(!empty($from_info[0]->full)){
                    $from_name = $from_info[0]->full;
                }
                else{
                    $from_name = $from_info[0]->mail;
                }
                $form_mail = $from_info[0]->mail;
                $msg_subject = $this->remove_emoji($message->getSubject());
                $body_content = $this->remove_emoji($message->getHTMLBody());


                $mail_startDate = \Carbon\Carbon::createFromFormat('Y-m-d H:i:s', $last_email_update);
                $mail_endDate = \Carbon\Carbon::createFromFormat('Y-m-d H:i:s', $last_update_time);

                $mail_check = \Carbon\Carbon::parse($message->getDate())->between($mail_startDate,$mail_endDate);


                // $body_content = Emoji::removeEmoji($body_content);
                if($mail_check){
                    $email = new Inbox([
                        'date' => $message->getDate(),
                        'from_name' => $from_name,
                        'from_email' => $form_mail,
                        'subject' => $msg_subject,
                        'body' => $body_content,
                        'user_id' => $logged_user->id,
                    ]);
                    $email->save();
                }
            }
                // echo '</tbody></table>';
        }
        $update_user->email_last_check = $last_update_time;
        $update_user->save();
        return 'Email Saved!';
    }
