    public function create_task($id){
        $inbox = Inbox::find($id);
        $task_name = $inbox->subject;
        $task_brief = $inbox->body;
        $task_status = 'new';
        $task_table = 'task';
        $department = '';
        $created_by = Auth::user()->name;
        switch ($inbox->user_id) {
            case '1':
                $department = 'Web';
                break;
            case '5':
                $department = 'Creative';
                break;  
            case '7':
                $department = 'Creative';
                break;
            case '8':
                $department = 'Paid';
                break;
            case '9':
                $department = 'Aggregator';
                break;
            case '10':
                $department = 'Paid';
                break;
            case '11':
                $department = 'Web';
                break;
            case '13':
                $department = 'Paid';
                break;
            case '15':
                $department = 'Organic';
                break;
            case '17':
                $department = 'Organic';
                break;
            case '27':
                $department = 'LMS';
                break;
        }
        DB::insert('insert into '.$task_table.' (name, brief, status, department, created_by, created_at) values (?, ?, ?, ?, ?, ?)', [$task_name, $task_brief, $task_status, $department, $created_by, now()]);
        $inbox->task = 'Task Created';
        $inbox->save();
        return Redirect::back();
    }

